import React, { useState } from "react";

function App() {
  const [list, setList] = useState([]);
  const [input, setInput] = useState("");
  const [errorMessage, setErrorMessage] = useState('');


  const deleteTodo = (id) => {
    const newList = list.filter((todo) => todo.id !== id);
    setList(newList);
  }

  // Görev ekleme işlevi
  const addTask = () => {
    if (input.trim() === '') {
      // Eğer görev boşsa hata mesajı göster
      setErrorMessage('Yapılacaklar girişine boş metin eklenemez.');
    } else {
      // Görevi listeye ekleyin ve hata mesajını temizleyin
      const newGorev = {
        id: Math.random(),
        gorev: input,
      };
      setList([...list, newGorev]);
      setInput('');
      setErrorMessage('Görev ekleme işlemi başarılı.');
    }
  };

  return (
    <div>
      <h1>Todo List</h1>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)} />

      <button onClick={addTask}>Ekle</button>
      {errorMessage && <p>{errorMessage}</p>}

      <ul>
        {list.map((todo) => (
          <li key={todo.id}>
            {todo.gorev}
            <button onClick={() => deleteTodo(todo.id)}>&times;</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
