    setEditedTask(id);

  };

  /*   const saveEditedTask = (id, newTask) => {
      const updatedList = list.map((todo) =>
        todo.id === id ? { ...todo, gorev: newTask } : todo

      );
      setList(updatedList);
      setEditedTask(null);
    };

   */

  /*  const addTask = () => {
     if (input.trim() === '') {

       alert("Yapılacaklar girişine boş metin eklenemez.");
     } else {
       const newGorev = {
         id: Math.random(),
         gorev: input,
       };
       setList([...list, newGorev]);
       setInput('');
       alert('Görev ekleme işlemi başarılı.');
     }
   };
  */

  /*   const addTask = () => {
      if (input.trim() === '') {
        alert("Yapılacaklar girişine boş metin eklenemez.");
      } else {
        const newGorev = {
          id: Math.random(),
          gorev: input,
        };
        setList([...list, newGorev]);
        setInput('');
        saveToLocalStorage([...list, newGorev]); // Veriyi localStorage'a kaydet
        alert('Görev ekleme işlemi başarılı.');
      }
    }; */

  /*   const addTask = () => {
      if (input.trim() === '') {
        alert("Görev girişine boş metin eklenemez.");
      } else if (estimatedTime.trim() === '') {
        alert("Tahmini süre girişine boş metin eklenemez.");
      } else {
        const newTask = {
          id: Math.random(),
          gorev: input,
          tahminiSaat: estimatedTime, // Tahmini süre ekleniyor
        };
        setList([...list, newTask]);
        setInput('');
        setEstimatedTime('');
        alert('Görev ekleme işlemi başarılı.');
      }
    };
   */

  /*   const addTask = () => {
      if (input.trim() === "") {
        alert("Görev girişine boş metin eklenemez.");
      } else if (!isValidTime(estimatedTime)) {
        alert("Geçerli bir tahmini süre girmelisiniz.");
      } else {
        const newTask = {
          id: Math.random(),
          gorev: input,
          tahminiSaat: estimatedTime,
        };
        setList([...list, newTask]);
        setInput("");
        setEstimatedTime("");
        alert("Görev ekleme işlemi başarılı.");
      }
    }; */


  const addTask = () => {
    if (input.trim() === "") {
      alert("Görev girişine boş metin eklenemez.");
    } else if (!isValidTime(estimatedTime)) {
      alert("Geçerli bir tahmini süre girmelisiniz.");
    } else {
      const newTask = {
        id: Math.random(),
        gorev: input,
        tahminiSaat: estimatedTime,
        harcananSaat: spentTime, // Harcanan süre
        tamamlandi: completed, // Görev tamamlandı durumu
      };
      setList([...list, newTask]);
      setInput("");
      setEstimatedTime("");
      setSpentTime("");
      setCompleted(false); // Varsayılan olarak görev tamamlanmamış olur
      alert("Görev ekleme işlemi başarılı.");
    }
  };







  const isValidTime = (timeStr) => {
    // Saat değerini doğrula (örneğin, "HH:MM" formatına uymalıdır)
    const timeRegex = /^([01]\d|2[0-3]):([0-5]\d)$/;
    return timeRegex.test(timeStr);
  };


  const saveEditedTask = (id, newTask) => {
    const updatedList = list.map((todo) =>
      todo.id === id ? { ...todo, gorev: newTask } : todo
    );
    setList(updatedList);
    setEditedTask(null);
    saveToLocalStorage(updatedList); // Veriyi localStorage'a kaydet
  };


  /*  useEffect(() => {
     const localDatas = JSON.parse(localStorage.getItem("taskList"));

     if (localDatas) {

       setList(localDatas);
     }
   }, []);

   useEffect(() => {
     localStorage.setItem("taskList", JSON.stringify(list));

   }, [list]); */

  const saveToLocalStorage = (data) => {
    localStorage.setItem("taskList", JSON.stringify(data));
  };

  const getFromLocalStorage = () => {
    const data = localStorage.getItem("taskList");
    return data ? JSON.parse(data) : [];
  };


  useEffect(() => {
    const localDatas = getFromLocalStorage();

    if (localDatas.length > 0 && list.length === 0) {
      setList(localDatas);
    }
  }, [list]);


  useEffect(() => {
    saveToLocalStorage(list);
  }, [list]);



  const saveEditedTime = (id, newTime) => {
    const updatedList = list.map((todo) =>
      todo.id === id ? { ...todo, tahminiSaat: newTime } : todo
    );
    setList(updatedList);
  };



  const toggleCompleted = (id) => {
    const updatedList = list.map((todo) =>
      todo.id === id ? { ...todo, tamamlandi: !todo.tamamlandi } : todo
    );
    setList(updatedList);
  };
  const toggleTimer = (id) => {
    const updatedList = list.map((todo) => {
      if (todo.id === id) {
        // Eğer timerRunning true ise durdur, false ise başlat
        return { ...todo, timerRunning: !todo.timerRunning };
      }
      return todo;
    });
    setList(updatedList);
  };

  useEffect(() => {
    const timerInterval = setInterval(() => {
      const updatedList = list.map((todo) => {
        if (todo.timerRunning) {
          // Timer çalışıyorsa harcanan süreyi arttır
          const newSpentTime = addTime(todo.harcananSaat);
          return { ...todo, harcananSaat: newSpentTime };
        }
        return todo;
      });
      setList(updatedList);
    }, 1000); // Her saniyede bir güncelle

    return () => {
      clearInterval(timerInterval); // Komponent kaldırıldığında zamanlayıcıyı temizle
    };
  }, [list]);



  const addTime = (timeStr) => {
    const [hours, minutes] = timeStr.split(":");
    const totalMinutes = parseInt(hours) * 60 + parseInt(minutes);
    const newTotalMinutes = totalMinutes + 1; // Bir saniye ekle
    const newHours = Math.floor(newTotalMinutes / 60);
    const newMinutes = newTotalMinutes % 60;
    return `${newHours.toString().padStart(2, "0")}:${newMinutes.toString().padStart(2, "0")}`;

  };



  const isListEmpty = list.length === 0;



  return (
    <div className="todo-container">

      <h1 style={{ color: isListEmpty ? "green" : "black" }}>Todo List</h1>





      <input
        className="todo-input"
        type="text"
        placeholder="Görev"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />
      <input
        className="todo-input"
        type="text"
        placeholder="Tahmini Süre (HH:MM)"
        value={estimatedTime}
        onChange={(e) => setEstimatedTime(e.target.value)}
      />



      <button style={{ backgroundColor: "green", color: "white" }} onClick={addTask}>EKLE</button>
      <table>
        <thead>
          <tr>
            <th style={{ marginRight: "10px" }}>Görev</th>
            <th style={{ marginLeft: "10px" }}>İşlemler</th>
          </tr>
        </thead>
        <tbody>



          {list.map((todo) => (
            <tr key={todo.id}>



              <td>
                {editedTask === todo.id ? (
                  <div>

                    <input
                      type="text"
                      value={todo.gorev}
                      onChange={(e) => saveEditedTask(todo.id, e.target.value)}

                    />
                    <input
                      type="text"
                      value={todo.tahminiSaat} // Tahmini süreyi görüntüle
                      onChange={(e) => saveEditedTime(todo.id, e.target.value)} // Tahmini süreyi kaydetmek için işlev ekleyin
                    />

                    <input
                      type="text"
                      value={todo.tahminiSaat}
                      onChange={(e) => saveEditedTime(todo.id, e.target.value)}
                    />

                    <button onClick={() => saveEditedTask(todo.id, todo.gorev)}>
                      KAYDET
                    </button>
                  </div>
                ) : (
                  <div>
                    <strong>{todo.gorev}</strong>
                    <br />
                    Tahmini Süre: {todo.tahminiSaat} saat {/* Tahmini süreyi görüntüle */}
                    <br />
                    Harcanan Süre: {todo.harcananSaat} saat
                  </div>
                )}
              </td>
              <td>
                <button style={{ backgroundColor: "red", color: "white", marginRight: "10px" }} onClick={() => deleteTodo(todo.id)}>SİL</button>

                <button style={{ backgroundColor: "yellow", color: "gray", marginLeft: "10px" }} onClick={() => editTask(todo.id)}>DÜZENLE</button>

                <button
                  style={{ backgroundColor: "blue", color: "white", marginLeft: "10px" }}
                  onClick={() => toggleTimer(todo.id)}
                >
                  {todo.timerRunning ? "Durdur" : "Başlat"}
                </button>

              </td>




            </tr>


          ))}



        </tbody>
      </table>
    </div>
  );

}

export default TodoList;
