import React, { useState, useEffect } from "react";

import "./TodoList.css";



function TodoList() {
  const [list, setList] = useState([]);
  const [input, setInput] = useState("");
  const [editedTask, setEditedTask] = useState(null);
  const [estimatedTime, setEstimatedTime] = useState(""); // Tahmini süre alanı







  const deleteTodo = (id) => {
    const newList = list.filter((todo) => todo.id !== id);

    setList(newList);
  }





  const editTask = (id) => {
    setEditedTask(id);

  };

  /*   const saveEditedTask = (id, newTask) => {
      const updatedList = list.map((todo) =>
        todo.id === id ? { ...todo, gorev: newTask } : todo

      );
      setList(updatedList);
      setEditedTask(null);
    };

   */

  /*  const addTask = () => {
     if (input.trim() === '') {

       alert("Yapılacaklar girişine boş metin eklenemez.");
     } else {
       const newGorev = {
         id: Math.random(),
         gorev: input,
       };
       setList([...list, newGorev]);
       setInput('');
       alert('Görev ekleme işlemi başarılı.');
     }
   };
  */

  /*   const addTask = () => {
      if (input.trim() === '') {
        alert("Yapılacaklar girişine boş metin eklenemez.");
      } else {
        const newGorev = {
          id: Math.random(),
          gorev: input,
        };
        setList([...list, newGorev]);
        setInput('');
        saveToLocalStorage([...list, newGorev]); // Veriyi localStorage'a kaydet
        alert('Görev ekleme işlemi başarılı.');
      }
    }; */

  const addTask = () => {
    if (input.trim() === '') {
      alert("Görev girişine boş metin eklenemez.");
    } else if (estimatedTime.trim() === '') {
      alert("Tahmini süre girişine boş metin eklenemez.");
    } else {
      const newTask = {
        id: Math.random(),
        gorev: input,
        tahminiSaat: estimatedTime, // Tahmini süre ekleniyor
      };
      setList([...list, newTask]);
      setInput('');
      setEstimatedTime('');
      alert('Görev ekleme işlemi başarılı.');
    }
  };


  const saveEditedTask = (id, newTask) => {
    const updatedList = list.map((todo) =>
      todo.id === id ? { ...todo, gorev: newTask } : todo
    );
    setList(updatedList);
    setEditedTask(null);
    saveToLocalStorage(updatedList); // Veriyi localStorage'a kaydet
  };


  /*  useEffect(() => {
     const localDatas = JSON.parse(localStorage.getItem("taskList"));

     if (localDatas) {

       setList(localDatas);
     }
   }, []);

   useEffect(() => {
     localStorage.setItem("taskList", JSON.stringify(list));

   }, [list]); */

  const saveToLocalStorage = (data) => {
    localStorage.setItem("taskList", JSON.stringify(data));
  };

  const getFromLocalStorage = () => {
    const data = localStorage.getItem("taskList");
    return data ? JSON.parse(data) : [];
  };


  useEffect(() => {
    const localDatas = getFromLocalStorage();

    if (localDatas.length > 0 && list.length === 0) {
      setList(localDatas);
    }
  }, [list]);


  useEffect(() => {
    saveToLocalStorage(list);
  }, [list]);



  const saveEditedTime = (id, newTime) => {
    const updatedList = list.map((todo) =>
      todo.id === id ? { ...todo, tahminiSaat: newTime } : todo
    );
    setList(updatedList);
  };







  const isListEmpty = list.length === 0;



  return (
    <div className="todo-container">

      <h1 style={{ color: isListEmpty ? "green" : "black" }}>Todo List</h1>



      <input
        className="todo-input"
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />


      <input
        className="todo-input"
        type="text"
        placeholder="Görev"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />
      <input
        className="todo-input"
        type="text"
        placeholder="Tahmini Süre (saat)"
        value={estimatedTime}
        onChange={(e) => setEstimatedTime(e.target.value)}
      />




      <button style={{ backgroundColor: "green", color: "white" }} onClick={addTask}>EKLE</button>
      <table>
        <thead>
          <tr>
            <th style={{ marginRight: "10px" }}>Görev</th>
            <th style={{ marginLeft: "10px" }}>İşlemler</th>
          </tr>
        </thead>
        <tbody>



          {list.map((todo) => (
            <tr key={todo.id}>



              <td>
                {editedTask === todo.id ? (
                  <div>

                    <input
                      type="text"
                      value={todo.gorev}
                      onChange={(e) => saveEditedTask(todo.id, e.target.value)}

                    />
                    <input
                      type="text"
                      value={todo.tahminiSaat} // Tahmini süreyi görüntüle
                      onChange={(e) => saveEditedTime(todo.id, e.target.value)} // Tahmini süreyi kaydetmek için işlev ekleyin
                    />

                    <button onClick={() => saveEditedTask(todo.id, todo.gorev)}>
                      KAYDET
                    </button>
                  </div>
                ) : (
                  <div>
                    <strong>{todo.gorev}</strong>
                    <br />
                    Tahmini Süre: {todo.tahminiSaat} saat {/* Tahmini süreyi görüntüle */}
                  </div>
                )}
              </td>
              <td>
                <button style={{ backgroundColor: "red", color: "white", marginRight: "10px" }} onClick={() => deleteTodo(todo.id)}>SİL</button>

                <button style={{ backgroundColor: "yellow", color: "gray", marginLeft: "10px" }} onClick={() => editTask(todo.id)}>DÜZENLE</button>
              </td>




            </tr>


          ))}



        </tbody>
      </table>
    </div>
  );

}

export default TodoList;
