import React, { useState } from "react";

function TodoList() {
  const [list, setList] = useState([]);
  const [input, setInput] = useState("");


  const deleteTodo = (id) => {
    const newList = list.filter((todo) => todo.id !== id);
    setList(newList);
  }

  // Görev ekleme işlevi
  const addTask = () => {
    if (input.trim() === '') {
      // Eğer görev boşsa hata mesajı göster

      alert("Yapılacaklar girişine boş metin eklenemez.");
    } else {
      // Görevi listeye ekleyin ve hata mesajını temizleyin
      const newGorev = {
        id: Math.random(),
        gorev: input,
      };
      setList([...list, newGorev]);
      setInput('');
      alert('Görev ekleme işlemi başarılı.');
    }
  };

  // Listedeki işlerin sayısını kontrol edin
  const isListEmpty = list.length === 0;


  return (
    <div>
      <h1 style={{ color: isListEmpty ? "green" : "black" }}>To-Do List</h1>
      <h1 >Todo List</h1>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)} />

      <button onClick={addTask}>Ekle</button>


      <ul>
        {list.map((todo) => (
          <li key={todo.id}>
            {todo.gorev}
            <button onClick={() => deleteTodo(todo.id)}>&times;</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
