import React, { useState } from "react";

function App() {
  const [list, setList] = useState([]);
  const [input, setInput] = useState("");

  const addTodo = (todo) => {
    const newTodo = {
      id: Math.random(),
      todo: todo,
    };

    //add the todo to the list
    setList([...list, newTodo]);


    //clear input box
    setInput("");
  };

  const deleteTodo = (id) => {
    const newList = list.filter((todo) => todo.id != id
    );
    setList(newList);
  }



  // Görev ekleme işlevi
  const addTask = () => {
    if (task.trim() === '') {
      // Eğer görev boşsa hata mesajı göster
      setErrorMessage('Yapılacaklar girişine boş metin eklenemez.');
    } else {
      // Görevi listeye ekleyin ve hata mesajını temizleyin
      const newGorev = {
        id: Math.random(),
        gorev: task,
      };
      setList([...list, newGorev]);
      setTask('');
      setErrorMessage('Görev ekleme işlemi başarılı.');

    }
  };





  return (
    <div>
      <h1>Todo List</h1>
      <input type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)} />
      <button onClick={() => addTodo(input)}>Add</button>

      <ul>
        {list.map((todo) => (
          <li key={todo.id}>
            {todo.todo}
            <button onClick={() => deleteTodo(todo.id)} >&times;</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
//TASARIM DEĞİŞTİR, GÜNCELLE EKLE.

/*Ekle butonuna basLğımda girilen veri boş ilse listeye ekleme yapmayacak ve ekrana alert 
ile “Yapılacaklar girişine boş meHn eklenemez” uyarısını verecek.*/

/* Eğer listede hiçbir iş yoksa To-Do List yazısını “Yeşil”, varsa “Siyah” olarak gösteriniz. */

/*Liste localstorage a kaydedilir. Sayfa yenilense bile listedeki elemanlar kaybolmaz*/
export default App;
